INSERT INTO `heros` VALUES (1,'Hogan','http://lilithimage.lilithcdn.com/afk/imgs/ChOgZAvwBDfqaULYyUZCbTMifTKbYILZ.png','http://lilithimage.lilithcdn.com/afk/imgs/bSuEAM7kbHv9yRnp9x4wBL2TMMexoVqb.png','http://lilithimage.lilithcdn.com/afk/imgs/M3RBqCYRMLaFdJHzGGZEJfIFeKPddwth.png','\"Protect the weak and uphold justice, no matter the cost!\"','Knight of the Kingdom','LIGHTBEARERS',1,'2019-05-27 06:22:35','2019-05-27 06:22:35',NULL,NULL,NULL,NULL,NULL,NULL),(2,'Ira','http://lilithimage.lilithcdn.com/afk/imgs/mSidL4jj3dgb3JPPF4wsfN0JCS28ScTR.png','http://lilithimage.lilithcdn.com/afk/imgs/T16Yfmc8VF5BQMeeuG5aJWEPyDHfttXk.png','http://lilithimage.lilithcdn.com/afk/imgs/ifLjmJT181nZf7Dc0U3fQIkJqkwh4XYh.png','“Take a step closer to the tree\'s edge. Only one of us knows it will be your last.” ','Rogue of the Forest','WILDERS',3,'2019-05-27 06:22:35','2019-05-27 06:22:35',NULL,NULL,NULL,NULL,NULL,NULL),(3,'Angelo','http://lilithimage.lilithcdn.com/afk/imgs/YfDedIA6T88mx3ZTn32h1cEWPv58dj0l.png','http://lilithimage.lilithcdn.com/afk/imgs/tAvC2XeUHbD0j6GKPWz3dxVizkVKghuS.png','http://lilithimage.lilithcdn.com/afk/imgs/xk4XoLN8qKITxWW3I3PODTcFGaqe9sN2.png','“ A caged bird is beautiful, but never so much as in flight”','Song of Dawn','LIGHTBEARERS',1,'2019-05-27 06:22:35','2019-05-27 06:22:35',NULL,NULL,NULL,NULL,NULL,NULL),(4,'Estrilda','http://lilithimage.lilithcdn.com/afk/imgs/LKMEm778g2L6UDWqVUpr0OtPGqQQsyZy.png','http://lilithimage.lilithcdn.com/afk/imgs/kYwGURW2oBizRvwLt2N4UXy9ztVOamkC.png','http://lilithimage.lilithcdn.com/afk/imgs/F8NyAYY1E8DxN0u1mcP1vcWw3pIGrRyT.png','\"A valiant heart has no room for fear\"','Knight of Valor','LIGHTBEARERS',1,'2019-05-27 06:22:35','2019-05-27 06:22:35',NULL,NULL,NULL,NULL,NULL,NULL),(5,'Nemora','http://lilithimage.lilithcdn.com/afk/imgs/2fwRfW9wbrWbIYLd3sYj1eEbu43eW07T.png','http://lilithimage.lilithcdn.com/afk/imgs/pEGIwF6gPn8ipXh82FkPCDxpcI8wT0Dt.png','http://lilithimage.lilithcdn.com/afk/imgs/dtjAEav13w3WRjuVTKY3P3bEA0i7Qmuq.png','